import Foundation

/////////////////////////////
// Paste your receipt here //
/////////////////////////////
let receipt = ""

guard let receiptData = Data(base64Encoded: receipt) else {
    Logger.error("Receipt is not valid base64-encoded data.")
    exit(1)
}

let rp = ReceiptParser.init()

let parsed: AppleReceipt

do {
    parsed = try rp.parse(from: receiptData)
} catch {
    Logger.error("Receipt could not be parsed.")
    exit(1)
}

Logger.info(parsed.description)
exit(0)
